INSERT INTO `backstage` VALUES (1,'231193','[{\"audition_times\": [], \"city\": \"Manhattan\", \"country\": \"235\", \"country_display\": \"United States\", \"region_str\": \"Manhattan, NY\", \"state\": \"NY\"}]','Paid','Casting the dramatic short film \"Knight Takes King.\" Synopsis: The film takes place in the Tidewater, a district located just outside the modern borders of the prosperous city Nova. Though it was initially part of the city, the Tide fell gradually into lawlessness as gentrification and resources centralized in Nova proper, and eventually the district line became a walled border. As infighting and crime leave the Tide embroiled in brutal chaos, some members of the more powerful factions are attempting to persuade Nova to reintegrate the district to better the lives of all.','2018-06-15T23:59:00','2018-06-06T10:59:41','','','[{\"parent_id\": 54, \"id\": 73, \"name\": \"Student Films\"}, {\"parent_id\": 54, \"id\": 72, \"name\": \"Short Films\"}]','\'Knight Takes King\'','[{\"max_age\": 40, \"gender_display\": \"Male\", \"name\": \"Glover\", \"requires_nudity\": false, \"url\": \"/casting/knight-takes-king-231193/glover-745559/\", \"gender\": \"M\", \"role_type_display\": \"Supporting\", \"min_age\": 25, \"transgender\": false, \"id\": 745559, \"role_type\": \"S\", \"description\": \"a member of Gray\'s crew who is central in the turning point in the story. No lines currently, but may have lines for a follow-on shoot in the fall. List any film/stage combat experience.\"}, {\"max_age\": 40, \"gender_display\": \"Male\", \"name\": \"John\", \"requires_nudity\": false, \"url\": \"/casting/knight-takes-king-231193/john-745560/\", \"gender\": \"M\", \"role_type_display\": \"Supporting\", \"min_age\": 25, \"transgender\": false, \"id\": 745560, \"role_type\": \"S\", \"description\": \"a member of Gray\'s crew who is central in the turning point in the story. No lines currently, but may have lines for a follow-on shoot in the fall. List any film/stage combat experience.\"}, {\"max_age\": 40, \"gender_display\": \"Male\", \"name\": \"Runner\", \"requires_nudity\": false, \"url\": \"/casting/knight-takes-king-231193/runner-745565/\", \"gender\": \"M\", \"role_type_display\": \"Day Player\", \"min_age\": 25, \"transgender\": false, \"id\": 745565, \"role_type\": \"D\", \"description\": \"a diplomatic member of a reunification meeting with Nova. Role includes a line and a brief fight scene. List any film/stage combat experience.\"}, {\"max_age\": 100, \"gender_display\": \"N/A\", \"name\": \"Faction Leader\", \"requires_nudity\": false, \"url\": \"/casting/knight-takes-king-231193/faction-leader-745581/\", \"gender\": \"N\", \"role_type_display\": \"Background / Extra\", \"min_age\": 35, \"transgender\": false, \"id\": 745581, \"role_type\": \"X\", \"description\": \"the faction leaders are the heads of the Tidewater communities--battle-hardened and strong, their influence is obvious at a glance. This is a heavily featured background role.\"}, {\"max_age\": 100, \"gender_display\": \"N/A\", \"name\": \"Faction Leader Bodyguard\", \"requires_nudity\": false, \"url\": \"/casting/knight-takes-king-231193/faction-leader-bodyguard-745582/\", \"gender\": \"N\", \"role_type_display\": \"Background / Extra\", \"min_age\": 35, \"transgender\": false, \"id\": 745582, \"role_type\": \"X\", \"description\": \"faction leaders in the Tidewater travel with their bodyguards constantly for protection, and their bodyguards are some of their most trusted peers. This role will be heavily featured in the reunification meetings.\"}, {\"max_age\": 30, \"gender_display\": \"N/A\", \"name\": \"Adira\'s Crew Member\", \"requires_nudity\": false, \"url\": \"/casting/knight-takes-king-231193/adiras-crew-member-745583/\", \"gender\": \"N\", \"role_type_display\": \"Background / Extra\", \"min_age\": 18, \"transgender\": false, \"id\": 745583, \"role_type\": \"X\", \"description\": \"these members of Adira\'s crew are present for a pivotal turn in the